const { equal } = require('assert')
const subject = require('..')

equal(subject({ type: 'Literal', value: 42 }), 42)
equal(subject({ type: 'Literal', value: -1 }), -1)
equal(subject({ type: 'Literal', value: 0 }), 0)
equal(subject({ type: 'Literal', value: 1 }), 1)
equal(subject({ type: 'Literal', value: -0.1 }), -0.1)
equal(subject({ type: 'Literal', value: 0.0 }), 0.0)
equal(subject({ type: 'Literal', value: 0.1 }), 0.1)
