const { deepEqual } = require('assert')
const subject = require('..')

deepEqual(subject({
  type: 'ObjectExpression',
  properties: [
    {
      type: 'Property',
      key: { type: 'Identifier', name: 'foo' },
      value: { type: 'Literal', value: 42 }
    }
  ]
}), { foo: 42 })

deepEqual(subject({
  type: 'ObjectExpression',
  properties: [
    {
      type: 'Property',
      key: { type: 'Literal', value: 'foo' },
      value: { type: 'Literal', value: 42 }
    }
  ]
}), { foo: 42 })

deepEqual(subject({
  type: 'ObjectExpression',
  properties: [
    {
      type: 'Property',
      key: { type: 'Identifier', name: 'foo' },
      value: {
        type: 'ObjectExpression',
        properties: [
          {
            type: 'Property',
            key: { type: 'Identifier', name: 'bar' },
            value: { type: 'Literal', value: 42 }
          }
        ]
      }
    }
  ]
}), { foo: { bar: 42 } })
