const { deepEqual } = require('assert')
const subject = require('..')

deepEqual(subject({
  type: 'NewExpression',
  callee: {
    type: 'Identifier',
    name: 'Set'
  },
  arguments: []
}), new Set())

deepEqual(subject({
  type: 'NewExpression',
  callee: {
    type: 'Identifier',
    name: 'Set'
  },
  arguments: [
    { type: 'Literal', value: 1 },
    { type: 'Literal', value: 2 },
    { type: 'Literal', value: 3 }
  ]
}), new Set([1, 2, 3]))
