const { deepEqual } = require('assert')
const subject = require('..')

deepEqual(subject({
  type: 'NewExpression',
  callee: {
    type: 'Identifier',
    name: 'WeakMap'
  },
  arguments: []
}), new WeakMap())

deepEqual(subject({
  type: 'NewExpression',
  callee: {
    type: 'Identifier',
    name: 'WeakMap'
  },
  arguments: [
    {
      type: 'ArrayExpression',
      elements: [
        { type: 'ObjectExpression', properties: [] },
        { type: 'Literal', value: 'value1' }
      ]
    }
  ]
}), new WeakMap([
  [{}, 'foo']
]))
