const { deepEqual } = require('assert')
const subject = require('..')

deepEqual(subject({
  type: 'NewExpression',
  callee: {
    type: 'Identifier',
    name: 'WeakSet'
  },
  arguments: []
}), new WeakSet())

deepEqual(subject({
  type: 'NewExpression',
  callee: {
    type: 'Identifier',
    name: 'WeakSet'
  },
  arguments: [
    { type: 'ObjectExpression', properties: [] }
  ]
}), new WeakSet([{}]))
